import React, { Component } from 'react'

class Add extends Component {

    render() {
        return (
            <div>
                <div>
                    <p>Add item</p>
                        <input 
                            value={this.props.included_item} 
                            type='text' 
                            placeholder='add item'
                            onChange={e => {
                                this.setState({ included_item: e.target.value })
                            }}
                        />
                    <button onClick={this.props.addingToTheList}>
                        Add new item
                    </button>
                    <button onClick={() => this.setState({ show_add: false })} type="button">Close</button>
                </div>
            </div>
        )
    }
}

export default Add
