export default class App extends Component {
  state = {
    value:'bread',
    shopping:'shopping',
    item_list: ['burger', 'bread', 'icecream'],
    show_add: false,
    show_delete: false


  }
  removeItem = ()=>{

    const valueToRemove = this.state.value;
    const filteredItems = this.item_list.filter(function(item) {
      return item !== valueToRemove
    })

    console.log(filteredItems)

  }
  render() {
    console.log(this.state.value)

    const addItem = <div>
        <div>
            <p>Add item</p>
            <input></input>
            <button onClick={() => this.setState({ show_add: false })} type="button">Close!</button>
            <button/>
        </div>
  </div>

    const deleteItem =  <div>
      <p>Delete item</p>
      <button onClick={() => this.setState({ show_delete: false })} type="button">Close!</button>
    </div>
  return (
      <div>
        <h1>Shopping</h1>
        <button onClick={() => {
          this.setState({ show_add: true})
        }}>Add</button>

        {
          this.state.show_delete?
          deleteItem:
          this.state.show_add ?
          addItem :
          <div>
             {this.state.item_list.map(item => (
                <li key={item}>{item} 
                    <button onClick={this.removeItem}>Edit</button>
                    <button onClick={() => {
                      this.setState({value:item})



                    }
                    }>Delete</button>
                </li>
              ))}
          </div>
        }




      </div>
    )
  }
}
