import React, { Component } from 'react'

class Add extends Component {

    render() {
        return (
            <div>
                <div>
                    <p>Add item</p>
                        <input 
                            value={this.props.included_item} 
                            type='text' 
                            placeholder='add item'
                            onChange={(e) => {
                                this.props.includedItem(e);
                            }}
                        />
                    <button onClick={this.props.addingToTheList}>
                        Add new item
                    </button>
                    <button onClick={this.props.backToHome} type="button">Close</button>
                </div>
            </div>
        )
    }
}
class Edit extends Component {

    render() {
        return (
            <div>
  <div>
      <p>edit item</p>
      <input 
          value={this.state.edited_list_value} 
          type='text' 
          placeholder='update'
          onChange={e => {
          this.setState({ edited_list_value: e.target.value })
          }}
        />
        <button onClick={this.editItem}>
           update
        </button>
         <button onClick={() => this.setState({ show_edit: false })} type="button">Close</button>
    </div>
</div>
        )
    }
}
export default {Edit, Add}
