// export default App;


class AddEdit extends Component {
  render() {
    return (
      <div>
          <div>
              <p>Add item</p>
              <input></input>
              <button onClick={this.props.closeAdd} type="button">Click Me!</button>
              <button/>
          </div>

      </div>
    )
  }
}
export default class App extends Component {
  state = {
    included_item: '',
    edited_list_value: '',
    index_to_edit:'',
    value:'bread',
    shopping:'shopping',
    item_list: ['burger', 'bread', 'icecream'],
    show_add: false,
    show_delete: false,
    show_edit: false
  }
  removeItem = (index) => {
    this.state.item_list.splice(index, 1);
    this.setState(this.state)
  }
  addingToTheList = () => {
    let new_list = [this.state.included_item, ...this.state.item_list];
    this.setState({item_list:new_list})
    console.log(this.state.item_list)

  }
  editItem = () =>  {
    this.state.item_list[this.state.index_to_edit] = this.state.edited_list_value
    this.setState(this.state)
  }
  render() {
    const addItem = <div>
        <div>
            <p>Add item</p>
            <input 
                value={this.state.included_item} 
                type='text' 
                placeholder='add item'
                onChange={e => {
                this.setState({ included_item: e.target.value })
                }}
              />
              <button onClick={this.addingToTheList}>
                 Add new item
              </button>
            <button onClick={() => this.setState({ show_add: false })} type="button">Close!</button>

        </div>
  </div>
  const editValue = <div>
  <div>
      <p>edit item</p>
      <input 
          value={this.state.edited_list_value} 
          type='text' 
          placeholder='update'
          onChange={e => {
          this.setState({ edited_list_value: e.target.value })
          }}
        />
        <button onClick={this.editItem}>
           update
        </button>
         <button onClick={() => this.setState({ show_edit: false })} type="button">Close!</button>
    </div>
</div>
  const deleteItem =  <div>
      <p>Delete item</p>
      <button onClick={() => this.setState({ show_delete: false })} type="button">Close!</button>
    </div>
  return (
      <div>
        <h1>Shopping</h1>
        <button onClick={() => {
          this.setState({ show_add: true})
        }}>Add</button>

        {
          this.state.show_delete?
          deleteItem:
          this.state.show_add ?
          addItem :
          this.state.show_edit ?
          editValue:
      <div>
             {this.state.item_list.map((item, index) => (
                <ul key={item}>{item} 
                    <button onClick={() => {
                      this.setState({index_to_edit:index, show_edit:true, edited_list_value: item})
                    }}>Edit</button>
                    <button onClick={()  => {
                      this.removeItem(index)
                    }}>Delete</button>
                </ul>
              ))}
          </div>
        }
    </div>
    )
  }
}
