class AddEdit extends Component {
  render() {
    return (
      <div>
          <div>
              <p>Add item</p>
              <input></input>
              <button onClick={this.props.closeAdd} type="button">Click Me!</button>
              <button/>
          </div>

      </div>
    )
  }
}
export default class App extends Component {
  state = {
    shopping:'shopping',
    item_list: ['burger', 'bread', 'icecream'],
    show_add: false,
    show_delete: false


  }

  render() {

    const addItem = <div>
        <div>
            <p>Add item</p>
            <input></input>
            <button onClick={() => this.setState({ show_add: false })} type="button">Close!</button>
            <button/>
        </div>



    </div>
    const deleteItem =  <div>
      <p>Delete item</p>
      <button onClick={() => this.setState({ show_delete: false })} type="button">Close!</button>
    </div>



    return (
      <div>
        <h1>Shopping</h1>
        <button onClick={() => {
          this.setState({ show_add: true})
        }}>Add</button>

        {
          this.state.show_add ?
         addItem :
          <div>
            {this.state.item_list.map((key, item)=>{
              <ul key={key}>{item}</ul>

            })
            }


          </div>
        }

        {
          this.state.show_delete ?
         deleteItem :
          <div>

          </div>
        }
      </div>
    )
  }
}
