  state = {
    shopping:'shopping',
    item_list: [],
    show_add: false


  }

  render() {

    const addItem = <div>
        <div>
            <p>Add item</p>
            <input></input>
            <button onClick={() => this.setState({ show_add: false })} type="button">Close!</button>
            <button/>
