export const _storeData = (key, value) => {
    return localStorage.setItem(key, value);
};

export const _retrieveData = (key) => {
    let value = localStorage.getItem(key);
    return value;
};

export const _removeData = (key) => {
    return localStorage.removeItem(key);
};
